UPDATE `SecondaryMarketBuyRequest` BR SET `Chng_ID` = (SELECT MAX(`Chng_ID`) FROM `SecondaryMarketBuyRequestHist` WHERE `Id` = BR.`Id`) WHERE `D_Name` IN ('example0604.ie', 'example0605.ie', 'example0606.ie', 'example0607.ie', 'example0608.ie', 'example0609.ie');

UPDATE `SecondaryMarketSellRequest` SR SET `Chng_ID` = (SELECT MAX(`Chng_ID`) FROM `SecondaryMarketSellRequestHist` WHERE `Id` = SR.`Id`) WHERE `BuyRequest_Id` IN (SELECT `Id` FROM `SecondaryMarketBuyRequest` WHERE `D_Name` IN ('example0605.ie', 'example0606.ie'));

